mapper.select:
select IFNULL(min(brd_seq_no), '')
  from brd
 where brd_type=#{brd_type}
   and brd_seq_no like #{brd_seq_no}
   and depth=#{depth}

select a.brd_seq_no, a.brd_type, a.brd_title, a.brd_hit,
a.reg_id, a.reg_nm, depth, concat(substr(a.reg_dt,1,4),'-',substr(a.reg_dt,5,2),'-',substr(a.reg_dt,7,2)) as reg_dt, a.udt_id, a.udt_nm, a.udt_dt from brd a where 1=1
and brd_title like #{src_title}
order by brd_seq_no desc limit #{firstRecordIndex}, #{recordCountPerPage}

select a.prd_seq_no, a.cate_seq_no, (select cate_nm from product_cate where cate_seq_no = a.cate_seq_no) as cate_nm, a.prd_nm, a.prd_price, a.prd_save, a.prd_img, a.prd_content, a.reg_id, a.reg_nm, a.reg_dt, a.udt_id, a.udt_nm, a.udt_dt from product a where 1=1
and prd_nm like #{src_title}
order by prd_seq_no desc

SELECT count(*) as CNT
FROM   product_cmmt
where prd_seq_no = #{prd_seq_no}
